contracts/SavingBank_v2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

interface ILiquidityVault {
    function payInterest(address user, uint256 amount) external;
    function getBalance() external view returns (uint256);
    function getActualBalance() external view returns (uint256);
}

interface ISavingBankNFT {
    function mint(
        address to,
        uint256 tokenId,
        uint256 planId,
        uint256 depositAmount
    ) external;
    function burn(uint256 tokenId) external;
    function ownerOf(uint256 tokenId) external view returns (address);
}

contract SavingBank is Ownable, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    /*//////////////////////////////////////////////////////////////
                                ERRORS
    //////////////////////////////////////////////////////////////*/
    error InvalidToken();
    error InvalidVault();
    error InvalidNFT();
    error NotEnabledPlan();
    error InvalidAmount();
    error InvalidAddress();
    error InvalidTenor();
    error InvalidAPR();
    error InvalidMinDeposit();
    error InvalidMaxDeposit();
    error NotExceed();
    error InvalidPlanId();
    error NotOwner();
    error NotActiveDeposit();
    error NotMaturedYet();
    error AlreadyMatured();
    error AlreadyRenewed();

    /*//////////////////////////////////////////////////////////////
                                ENUMS
    //////////////////////////////////////////////////////////////*/
    enum DepositStatus {
        Active,
        Withdrawn,
        EarlyWithdrawn,
        Renewed
    }

    /*//////////////////////////////////////////////////////////////
                               STRUCTS
    //////////////////////////////////////////////////////////////*/
    struct SavingPlan {
        uint256 tenorDays;
        uint256 aprBps;
        uint256 minDeposit;
        uint256 maxDeposit;
        uint256 earlyWithdrawPenaltyBps;
        bool enabled;
    }

    struct DepositCertificate {
        address owner;
        uint256 planId;
        uint256 principal;
        uint256 startAt;
        uint256 maturityAt;
        DepositStatus status;
        uint256 renewedDepositId;
        // SNAPSHOT PLAN DATA
        uint256 snapshotAprBps;
        uint256 snapshotTenorDays;
        uint256 snapshotEarlyWithdrawPenaltyBps;
    }

    /*//////////////////////////////////////////////////////////////
                              CONSTANTS
    //////////////////////////////////////////////////////////////*/
    uint256 private constant SECONDS_PER_YEAR = 365 days;
    uint256 private constant BASIS_POINTS = 10000;

    /*//////////////////////////////////////////////////////////////
                           STATE VARIABLES
    //////////////////////////////////////////////////////////////*/
    IERC20 public immutable token;
    ISavingBankNFT public nft;

    uint256 public nextPlanId;
    uint256 public nextDepositId;

    mapping(uint256 => SavingPlan) public savingPlans;
    mapping(uint256 => DepositCertificate) public depositCertificates;
    mapping(address => uint256[]) public userDepositIds;

    ILiquidityVault public vault;
    address public feeReceiver;

    /*//////////////////////////////////////////////////////////////
                             CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/
    constructor(
        address _token,
        address _vault,
        address _nft,
        address _feeReceiver
    ) Ownable(msg.sender) {
        if (_token == address(0)) revert InvalidToken();
        if (_vault == address(0)) revert InvalidVault();
        if (_nft == address(0)) revert InvalidNFT();
        if (_feeReceiver == address(0)) revert InvalidAddress();

        token = IERC20(_token);
        vault = ILiquidityVault(_vault);
        nft = ISavingBankNFT(_nft);
        feeReceiver = _feeReceiver;

        nextPlanId = 1;
        nextDepositId = 1;
    }

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/
    event PlanCreated(
        uint256 indexed planId,
        uint256 tenorDays,
        uint256 aprBps,
        uint256 minDeposit,
        uint256 maxDeposit,
        uint256 earlyWithdrawPenaltyBps
    );
    event FeeReceiverUpdated(address indexed newFeeReceiver);
    event PlanUpdated(uint256 indexed planId, bool isEnabled);
    event VaultUpdated(address indexed newVault);
    event NFTUpdated(address indexed newNFT);
    event DepositCertificateOpened(
        uint256 indexed depositId,
        address indexed user,
        uint256 indexed planId,
        uint256 depositAmount,
        uint256 maturityTimestamp
    );
    event Withdrawn(
        uint256 indexed depositId,
        address indexed user,
        uint256 principalAmount,
        uint256 interestAmount,
        DepositStatus finalStatus
    );
    event EarlyWithdrawn(
        uint256 indexed depositId,
        address indexed user,
        uint256 amountReceived,
        uint256 penaltyAmount,
        DepositStatus finalStatus
    );
    event Renewed(
        uint256 indexed oldDepositId,
        uint256 indexed newDepositId,
        uint256 newPrincipal
    );

    /*//////////////////////////////////////////////////////////////
                          CORE FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Open a new deposit certificate
     */
    function openDepositCertificate(
        uint256 planId,
        uint256 depositAmount
    ) external whenNotPaused nonReentrant {
        SavingPlan memory plan = savingPlans[planId];

        if (!plan.enabled) revert NotEnabledPlan();
        if (depositAmount < plan.minDeposit) revert InvalidAmount();
        if (plan.maxDeposit > 0 && depositAmount > plan.maxDeposit)
            revert InvalidAmount();

        address depositor = msg.sender;
        uint256 newDepositId = nextDepositId;
        uint256 maturityTimestamp = block.timestamp + (plan.tenorDays * 1 days);

        depositCertificates[newDepositId] = DepositCertificate({
            owner: depositor,
            planId: planId,
            principal: depositAmount,
            startAt: block.timestamp,
            maturityAt: maturityTimestamp,
            status: DepositStatus.Active,
            renewedDepositId: 0,
            snapshotAprBps: plan.aprBps,
            snapshotTenorDays: plan.tenorDays,
            snapshotEarlyWithdrawPenaltyBps: plan.earlyWithdrawPenaltyBps
        });

        userDepositIds[depositor].push(newDepositId);
        nextDepositId++;

        token.safeTransferFrom(depositor, address(this), depositAmount);

        // Mint NFT certificate with basic metadata
        nft.mint(depositor, newDepositId, planId, depositAmount);

        emit DepositCertificateOpened(
            newDepositId,
            depositor,
            planId,
            depositAmount,
            maturityTimestamp
        );
    }

    /**
     * @notice Withdraw at maturity (principal + interest)
     */
    function withdraw(uint256 depositId) external nonReentrant {
        DepositCertificate storage deposit = depositCertificates[depositId];

        if (deposit.owner != msg.sender) revert NotOwner();
        if (deposit.status != DepositStatus.Active) revert NotActiveDeposit();
        if (block.timestamp < deposit.maturityAt) revert NotMaturedYet();

        uint256 interest = _calculateInterest(depositId);

        deposit.status = DepositStatus.Withdrawn;

        vault.payInterest(msg.sender, interest);
        token.safeTransfer(msg.sender, deposit.principal);

        // Burn NFT
        nft.burn(depositId);

        emit Withdrawn(
            depositId,
            msg.sender,
            deposit.principal,
            interest,
            DepositStatus.Withdrawn
        );
    }

    /**
     * @notice Early withdraw with penalty
     */
    function earlyWithdraw(uint256 depositId) external nonReentrant {
        DepositCertificate storage deposit = depositCertificates[depositId];

        if (deposit.owner != msg.sender) revert NotOwner();
        if (deposit.status != DepositStatus.Active) revert NotActiveDeposit();
        if (block.timestamp >= deposit.maturityAt) revert AlreadyMatured();

        uint256 penalty = (deposit.principal *
            deposit.snapshotEarlyWithdrawPenaltyBps) / BASIS_POINTS;
        uint256 amountToReturn = deposit.principal - penalty;

        deposit.status = DepositStatus.EarlyWithdrawn;

        token.safeTransfer(feeReceiver, penalty);
        token.safeTransfer(msg.sender, amountToReturn);

        // Burn NFT
        nft.burn(depositId);

        emit EarlyWithdrawn(
            depositId,
            msg.sender,
            amountToReturn,
            penalty,
            DepositStatus.EarlyWithdrawn
        );
    }

    /**
     * @notice Renew deposit with same plan
     */
    function renewWithSamePlan(uint256 depositId) external nonReentrant {
        DepositCertificate storage oldDeposit = depositCertificates[depositId];

        if (msg.sender != oldDeposit.owner) revert NotOwner();
        if (block.timestamp < oldDeposit.maturityAt) revert NotMaturedYet();

        SavingPlan memory plan = savingPlans[oldDeposit.planId];
        if (!plan.enabled) revert NotEnabledPlan();

        uint256 interest = _calculateInterest(depositId);

        uint256 newPrincipal = oldDeposit.principal + interest;
        uint256 newId = nextDepositId;
        address user = msg.sender;
        uint256 maturity = block.timestamp + (plan.tenorDays * 1 days);

        oldDeposit.status = DepositStatus.Renewed;
        oldDeposit.renewedDepositId = newId;

        userDepositIds[user].push(newId);
        nextDepositId++;

        depositCertificates[newId] = DepositCertificate({
            owner: user,
            planId: oldDeposit.planId,
            principal: newPrincipal,
            startAt: block.timestamp,
            maturityAt: maturity,
            status: DepositStatus.Active,
            renewedDepositId: 0,
            snapshotAprBps: plan.aprBps,
            snapshotTenorDays: plan.tenorDays,
            snapshotEarlyWithdrawPenaltyBps: plan.earlyWithdrawPenaltyBps
        });

        vault.payInterest(address(this), interest); // Transfer interest to SavingBank for compound

        nft.burn(depositId);
        nft.mint(user, newId, oldDeposit.planId, newPrincipal);

        emit Renewed(depositId, newId, newPrincipal);
        emit DepositCertificateOpened(
            newId,
            user,
            oldDeposit.planId,
            newPrincipal,
            maturity
        );
    }

    /**
     * @notice Renew deposit with new plan
     */
    function renewWithNewPlan(
        uint256 depositId,
        uint256 newPlanId
    ) external nonReentrant {
        DepositCertificate storage oldDeposit = depositCertificates[depositId];

        if (oldDeposit.owner != msg.sender) revert NotOwner();
        if (oldDeposit.status != DepositStatus.Active)
            revert NotActiveDeposit();
        if (block.timestamp < oldDeposit.maturityAt) revert NotMaturedYet();
        if (oldDeposit.renewedDepositId != 0) revert AlreadyRenewed();

        SavingPlan memory plan = savingPlans[newPlanId];
        if (!plan.enabled) revert NotEnabledPlan();

        uint256 interest = _calculateInterest(depositId);

        uint256 newPrincipal = oldDeposit.principal + interest;
        uint256 newId = nextDepositId;
        address user = msg.sender;
        uint256 maturity = block.timestamp + (plan.tenorDays * 1 days);

        oldDeposit.status = DepositStatus.Renewed;
        oldDeposit.renewedDepositId = newId;

        userDepositIds[user].push(newId);
        nextDepositId++;

        vault.payInterest(address(this), interest); // Transfer interest to SavingBank for compound

        depositCertificates[newId] = DepositCertificate({
            owner: user,
            planId: newPlanId,
            principal: newPrincipal,
            startAt: block.timestamp,
            maturityAt: maturity,
            status: DepositStatus.Active,
            renewedDepositId: 0,
            snapshotAprBps: plan.aprBps,
            snapshotTenorDays: plan.tenorDays,
            snapshotEarlyWithdrawPenaltyBps: plan.earlyWithdrawPenaltyBps
        });

        // Burn old NFT and mint new NFT
        nft.burn(depositId);
        nft.mint(user, newId, newPlanId, newPrincipal);

        emit Renewed(depositId, newId, newPrincipal);
        emit DepositCertificateOpened(
            newId,
            user,
            newPlanId,
            newPrincipal,
            maturity
        );
    }

    /*//////////////////////////////////////////////////////////////
                          ADMIN FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    function setVault(address newVault) external onlyOwner {
        if (newVault == address(0)) revert InvalidVault();
        vault = ILiquidityVault(newVault);
        emit VaultUpdated(newVault);
    }

    function setNFT(address newNFT) external onlyOwner {
        if (newNFT == address(0)) revert InvalidNFT();
        nft = ISavingBankNFT(newNFT);
        emit NFTUpdated(newNFT);
    }

    function createPlan(
        uint256 tenorDays,
        uint256 aprBps,
        uint256 minDeposit,
        uint256 maxDeposit,
        uint256 earlyWithdrawPenaltyBps
    ) external onlyOwner {
        if (tenorDays == 0) revert InvalidTenor();
        if (aprBps == 0) revert InvalidAPR();
        if (minDeposit == 0) revert InvalidMinDeposit();
        if (maxDeposit > 0 && maxDeposit < minDeposit)
            revert InvalidMaxDeposit();
        if (
            earlyWithdrawPenaltyBps == 0 ||
            earlyWithdrawPenaltyBps > BASIS_POINTS
        ) revert NotExceed();

        uint256 newPlanId = nextPlanId;

        savingPlans[newPlanId] = SavingPlan({
            tenorDays: tenorDays,
            aprBps: aprBps,
            minDeposit: minDeposit,
            maxDeposit: maxDeposit,
            earlyWithdrawPenaltyBps: earlyWithdrawPenaltyBps,
            enabled: true
        });

        nextPlanId++;

        emit PlanCreated(
            newPlanId,
            tenorDays,
            aprBps,
            minDeposit,
            maxDeposit,
            earlyWithdrawPenaltyBps
        );
    }

    function updatePlanStatus(
        uint256 planId,
        bool isEnabled
    ) external onlyOwner {
        if (planId <= 0 || planId >= nextPlanId) revert InvalidPlanId();
        savingPlans[planId].enabled = isEnabled;
        emit PlanUpdated(planId, isEnabled);
    }

    function updatePlan(
        uint256 planId,
        uint256 tenorDays,
        uint256 aprBps,
        uint256 minDeposit,
        uint256 maxDeposit,
        uint256 earlyWithdrawPenaltyBps
    ) external onlyOwner {
        if (planId <= 0 || planId >= nextPlanId) revert InvalidPlanId();
        if (tenorDays == 0) revert InvalidTenor();
        if (aprBps == 0) revert InvalidAPR();
        if (minDeposit == 0) revert InvalidMinDeposit();
        if (maxDeposit > 0 && maxDeposit < minDeposit)
            revert InvalidMaxDeposit();
        if (
            earlyWithdrawPenaltyBps == 0 ||
            earlyWithdrawPenaltyBps > BASIS_POINTS
        ) revert NotExceed();

        SavingPlan storage plan = savingPlans[planId];
        plan.tenorDays = tenorDays;
        plan.aprBps = aprBps;
        plan.minDeposit = minDeposit;
        plan.maxDeposit = maxDeposit;
        plan.earlyWithdrawPenaltyBps = earlyWithdrawPenaltyBps;

        emit PlanUpdated(planId, plan.enabled);
    }

    function setFeeReceiver(address newFeeReceiver) external onlyOwner {
        if (newFeeReceiver == address(0)) revert InvalidAddress();
        feeReceiver = newFeeReceiver;
        emit FeeReceiverUpdated(newFeeReceiver);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    /*//////////////////////////////////////////////////////////////
                          VIEW FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    function getPlanInfo(
        uint256 planId
    )
        external
        view
        returns (
            uint256 tenorDays,
            uint256 aprBps,
            uint256 minDeposit,
            uint256 maxDeposit,
            uint256 earlyWithdrawPenaltyBps,
            bool enabled
        )
    {
        SavingPlan memory plan = savingPlans[planId];
        return (
            plan.tenorDays,
            plan.aprBps,
            plan.minDeposit,
            plan.maxDeposit,
            plan.earlyWithdrawPenaltyBps,
            plan.enabled
        );
    }

    function getUserDepositIds(
        address user
    ) external view returns (uint256[] memory) {
        return userDepositIds[user];
    }

    function getCalculateInterest(
        uint256 depositId
    ) external view returns (uint256) {
        return _calculateInterest(depositId);
    }

    function getDepositInfo(
        uint256 depositId
    )
        external
        view
        returns (
            address owner,
            uint256 planId,
            uint256 principal,
            uint256 startAt,
            uint256 maturityAt,
            DepositStatus status,
            uint256 renewedDepositId
        )
    {
        DepositCertificate memory deposit = depositCertificates[depositId];
        return (
            deposit.owner,
            deposit.planId,
            deposit.principal,
            deposit.startAt,
            deposit.maturityAt,
            deposit.status,
            deposit.renewedDepositId
        );
    }

    /*//////////////////////////////////////////////////////////////
                         HELPER FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    function _calculateInterest(
        uint256 depositId
    ) internal view returns (uint256) {
        DepositCertificate memory deposit = depositCertificates[depositId];

        uint256 tenorSeconds = deposit.snapshotTenorDays * 1 days;

        uint256 interestAmount = (deposit.principal *
            deposit.snapshotAprBps *
            tenorSeconds) / (SECONDS_PER_YEAR * BASIS_POINTS);

        return interestAmount;
    }
}

contracts/SavingBankNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Base64.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract SavingBankNFT is ERC721URIStorage, Ownable {
    using Strings for uint256;

    /*//////////////////////////////////////////////////////////////
                                ERRORS
    //////////////////////////////////////////////////////////////*/
    error Unauthorized();
    error InvalidAddress();
    error TokenNotExists();

    /*//////////////////////////////////////////////////////////////
                               STRUCTS
    //////////////////////////////////////////////////////////////*/
    struct CertificateData {
        uint256 depositId;
        uint256 planId;
        uint256 depositAmount;
        uint256 depositTime;
    }

    /*//////////////////////////////////////////////////////////////
                           STATE VARIABLES
    //////////////////////////////////////////////////////////////*/
    address public savingBank;

    // Mapping from token ID to certificate data
    mapping(uint256 => CertificateData) private _certificateData;

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/
    event SavingBankUpdated(address indexed oldBank, address indexed newBank);
    event CertificateMinted(uint256 indexed tokenId, address indexed owner);
    event CertificateBurned(uint256 indexed tokenId);

    /*//////////////////////////////////////////////////////////////
                              MODIFIERS
    //////////////////////////////////////////////////////////////*/
    modifier onlySavingBank() {
        if (msg.sender != savingBank) revert Unauthorized();
        _;
    }

    /*//////////////////////////////////////////////////////////////
                             CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/
    constructor()
        ERC721("Saving Bank Certificate", "SBC")
        Ownable(msg.sender)
    {}

    /*//////////////////////////////////////////////////////////////
                        SAVINGBANK FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Mint a new certificate NFT with metadata
     * @param to Address to mint the NFT to
     * @param tokenId Token ID for the NFT
     * @param planId Plan ID of the deposit
     * @param depositAmount Amount deposited
     */
    function mint(
        address to,
        uint256 tokenId,
        uint256 planId,
        uint256 depositAmount
    ) external onlySavingBank {
        _safeMint(to, tokenId);

        // Store certificate data
        _certificateData[tokenId] = CertificateData({
            depositId: tokenId,
            planId: planId,
            depositAmount: depositAmount,
            depositTime: block.timestamp
        });

        // Set token URI with on-chain metadata
        _setTokenURI(tokenId, _buildTokenURI(tokenId));

        emit CertificateMinted(tokenId, to);
    }

    /**
     * @notice Burn a certificate NFT
     * @param tokenId Token ID to burn
     */
    function burn(uint256 tokenId) external onlySavingBank {
        if (_ownerOf(tokenId) == address(0)) revert TokenNotExists();
        _burn(tokenId);
        delete _certificateData[tokenId];
        emit CertificateBurned(tokenId);
    }

    /*//////////////////////////////////////////////////////////////
                          ADMIN FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Set the SavingBank contract address
     * @param _savingBank Address of the SavingBank contract
     */
    function setSavingBank(address _savingBank) external onlyOwner {
        if (_savingBank == address(0)) revert InvalidAddress();
        address oldBank = savingBank;
        savingBank = _savingBank;
        emit SavingBankUpdated(oldBank, _savingBank);
    }

    /*//////////////////////////////////////////////////////////////
                          VIEW FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Get certificate data for a token
     * @param tokenId Token ID to query
     * @return Certificate data struct
     */
    function getCertificateData(
        uint256 tokenId
    ) external view returns (CertificateData memory) {
        return _certificateData[tokenId];
    }

    /*//////////////////////////////////////////////////////////////
                      SOULBOUND IMPLEMENTATION
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Override to prevent token transfers (soulbound certificates)
     * @dev Only allows minting and burning, no transfers
     */
    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal virtual override returns (address) {
        address from = _ownerOf(tokenId);

        // Allow minting (from == address(0)) and burning (to == address(0))
        if (from != address(0) && to != address(0)) {
            revert Unauthorized();
        }

        return super._update(to, tokenId, auth);
    }

    /*//////////////////////////////////////////////////////////////
                       METADATA GENERATION
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Build token URI with simple on-chain metadata
     * @param tokenId Token ID
     * @return Base64 encoded data URI
     */
    function _buildTokenURI(
        uint256 tokenId
    ) internal view returns (string memory) {
        CertificateData memory data = _certificateData[tokenId];

        // Format deposit amount (assuming 18 decimals)
        string memory formattedAmount = _formatAmount(data.depositAmount);

        // Build simple JSON metadata
        bytes memory json = abi.encodePacked(
            "{",
            '"name": "Saving Bank Certificate #',
            tokenId.toString(),
            '",',
            '"description": "Certificate of Deposit - Saving Bank Protocol",',
            '"image": "data:image/svg+xml;base64,',
            _buildSimpleSVG(tokenId),
            '",',
            '"attributes": [',
            "{",
            '"trait_type": "Deposit ID",',
            '"value": ',
            tokenId.toString(),
            "},",
            "{",
            '"trait_type": "Plan ID",',
            '"value": ',
            data.planId.toString(),
            "},",
            "{",
            '"trait_type": "Amount",',
            '"value": "',
            formattedAmount,
            '"',
            "},",
            "{",
            '"trait_type": "Deposit Time",',
            '"display_type": "date",',
            '"value": ',
            data.depositTime.toString(),
            "}",
            "]",
            "}"
        );

        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    Base64.encode(json)
                )
            );
    }

    /**
     * @notice Build simple SVG image
     * @param tokenId Token ID
     * @return Base64 encoded SVG
     */
    function _buildSimpleSVG(
        uint256 tokenId
    ) internal view returns (string memory) {
        CertificateData memory data = _certificateData[tokenId];
        string memory formattedAmount = _formatAmount(data.depositAmount);

        bytes memory svg = abi.encodePacked(
            '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 350 200">',
            // Background
            '<rect width="350" height="200" fill="#1a1a2e"/>',
            // Border
            '<rect x="10" y="10" width="330" height="180" rx="10" fill="none" stroke="#6C5CE7" stroke-width="2"/>',
            // Title
            '<text x="175" y="40" text-anchor="middle" font-family="Arial, sans-serif" font-size="18" font-weight="bold" fill="#A29BFE">',
            "DEPOSIT CERTIFICATE",
            "</text>",
            // Certificate Number
            '<text x="175" y="65" text-anchor="middle" font-family="monospace" font-size="12" fill="#888">',
            "#",
            tokenId.toString(),
            "</text>",
            // Amount Label
            '<text x="175" y="95" text-anchor="middle" font-family="Arial, sans-serif" font-size="11" fill="#888">',
            "AMOUNT",
            "</text>",
            // Amount Value
            '<text x="175" y="120" text-anchor="middle" font-family="Arial, sans-serif" font-size="20" font-weight="bold" fill="#fff">',
            formattedAmount,
            "</text>",
            // Plan ID
            '<text x="175" y="150" text-anchor="middle" font-family="Arial, sans-serif" font-size="11" fill="#888">',
            "Plan ID: ",
            data.planId.toString(),
            "</text>",
            // Footer
            '<text x="175" y="175" text-anchor="middle" font-family="Arial, sans-serif" font-size="9" fill="#555">',
            "Saving Bank Protocol",
            "</text>",
            "</svg>"
        );

        return Base64.encode(svg);
    }

    /**
     * @notice Format amount with decimals
     * @param amount Amount to format (18 decimals)
     * @return Formatted string
     */
    function _formatAmount(
        uint256 amount
    ) internal pure returns (string memory) {
        uint256 whole = amount / 1e18;
        uint256 decimals = (amount % 1e18) / 1e16; // 2 decimal places

        if (decimals == 0) {
            return string(abi.encodePacked(whole.toString(), " tokens"));
        }

        // Pad decimals to 2 digits
        string memory decimalStr = decimals < 10
            ? string(abi.encodePacked("0", decimals.toString()))
            : decimals.toString();

        return
            string(
                abi.encodePacked(whole.toString(), ".", decimalStr, " tokens")
            );
    }
}
